import { useState } from "react";
import "./style.css";

export const Form = ({ listTransactions, setListTransactions }) => {
  const [inputDescription, setInputDescription] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [selectValue, setSelectValue] = useState("entrada");

  const handleSubmit = (event) => {
    event.preventDefault();
    setListTransactions([
      ...listTransactions,
      {
        description: inputDescription,
        value: inputValue,
        type: selectValue,
      },
    ]);
  };

  return (

    <form className="form" onSubmit={handleSubmit}>
      <div className="form__description">
        <label className="form__description--label" htmlFor="name">
          Descrição
        </label>
        <input
          className="form__description--input" id="name"
          onChange={(event) => setInputDescription(event.target.value)}
          placeholder="Digite aqui sua descrição"
        />
      </div>
        <span className="form__description--span">Ex: Compras de Roupas</span>
      <div className="form__input__select">
        <div className="form__input--value">
          <label className="form__input--label" htmlFor="valor">
            Valor
          </label>
          <input
            className="form__input"
            type="text"
            id="valor"
            onChange={(event) => setInputValue(event.target.value)}
            placeholder="1"
          />
        </div>
        <div className="form__div--select">
          <label className="form__select--label" htmlFor="tipoDeValor">
            Tipo de Valor
          </label>
          <select
            className="form__select"
            type="select"
            id="tipoDeValor"
            onChange={(event) => setSelectValue(event.target.value)}
          >
            <option value="entrada">Entrada</option>
            <option value="saida">Saida</option>
          </select>
        </div>
      </div>
      <button className="btnPrimary" type="submit">
        Inserir valor
      </button>
    </form>
  );
};
