import { useState } from "react";
import { Form } from "./components/Form";
import { Header } from "./components/Header/";
import { HomePage} from './pages/HomePage'
import { List } from "./components/List";
import { TotalMoney } from "./components/TotalMoney";


function App() {
  const [listTransactions, setListTransactions] = useState([]);

  const [homePage, setHomePage] = useState(true)

  const [filter, setFilter] = useState([]);

  const filtered = (listTransactions, type, setFilter) => {
    setFilter(
      listTransactions.filter((transaction) => transaction.type === type)    );
  };

  return homePage ? (
      <HomePage setHomePage={setHomePage}/>
  ) : (
    <>
    <Header setHomePage={setHomePage} />
      <main className="main__container">
        <Form
          listTransactions={listTransactions}
          setListTransactions={setListTransactions}
        />
        {listTransactions.length > 0 ? (
            <TotalMoney listTransactions={listTransactions} />
          ) : 0}
          <div>
            <p>
            Resumo financeiro
            </p>
            <button onClick={() => setFilter(listTransactions)}>Todos</button>
            <button onClick={() => filtered(listTransactions, 'Entradas', setFilter)}>Entradas</button>
            <button onClick={() => filtered(listTransactions, 'Despesas', setFilter)}>Despesas</button>
          </div>

        <List listTransactions={listTransactions} setListTransactions={setListTransactions} setFilter={setFilter} filter={filter}/>
      </main>
    </>
  );
}

export default App;
