import { useState } from "react";
import { Card } from "../Card";
import "./style.css";
import NoCard from "../../assets/NoCard.png";
import { ButtonsFilter } from '../Buttons'

export const List = ({ listTransactions, setListT }) => {

  const [filter, setFilter] = useState("");
  return (
    <section className="section__cards">
      <div className="section__cards--elements">
        <h3 className="section__cards--title">Resumo financeiro</h3>
        <div className="section__cards--btns">
          <ButtonsFilter onClick={() => setFilter('')} className="btns" id="todos">Todosyarn</ButtonsFilter>
          <ButtonsFilter onClick={() => setFilter('entrada')} className="btns" id="entrada">Entradas</ButtonsFilter>
          <ButtonsFilter onClick={() => setFilter('saida')} className="btns" id="despesas">Despesas</ButtonsFilter>
        </div>
      </div>
        {listTransactions.length > 0 ? (
          <>
            {listTransactions.map((item, index) => (
              <Card
                key={index}
                description={item.description}
                type={item.type}
                value={item.value}
                ></Card>
                ))}
          </>
        ) : (
          <ul className="section__cards--ul">
            <h2 className="section__cards--text">
              Você ainda não possui nenhum lançamento
            </h2>
            <img className="logoVazia" src={NoCard} alt="logoCardVazio" />
            <img className="logoVazia" src={NoCard} alt="logoCardVazio" />
            <img className="logoVazia" src={NoCard} alt="logoCardVazio" />
          </ul>
        )}
    </section>
  );
};
