import { Card } from "../Card";
import "./style.css";
import NoCard from "../../assets/NoCard.png";
import { TotalMoney } from "../TotalMoney";

export const List = ({ listTransactions }) => {
  return (
    <section className="section__cards">
      <div className="section__cards--elements">
        <h3 className="section__cards--title">Resumo financeiro</h3>
        <div className="section__cards--btns">
          <button className="btns">Todos</button>
          <button className="btns">Entradas</button>
          <button className="btns">Despesas</button>
        </div>
      </div>
        {listTransactions.length > 0 ? (
          <>
            {listTransactions.map((item, index) => (
              <Card
                key={index}
                description={item.description}
                type={item.type}
                value={item.value}
                ></Card>
                ))}
                <TotalMoney value={item.value}/>
          </>
        ) : (
          <ul className="section__cards--ul">
            <h2 className="section__cards--text">
              Você ainda não possui nenhum lançamento
            </h2>
            <img className="logoVazia" src={NoCard} alt="logoCardVazio" />
            <img className="logoVazia" src={NoCard} alt="logoCardVazio" />
            <img className="logoVazia" src={NoCard} alt="logoCardVazio" />
          </ul>
        )}
    </section>
  );
};
