// import para mudança de estado
import { useState } from "react";

//component Form sendo chamdo para o componenete Pai App
import { Form } from "./components/Form";

//component Header sendo chamdo para o componenete Pai App
import { Header } from "./components/Header/";

//component HomePage sendo chamdo para o componenete Pai App
import { HomePage } from "./pages/HomePage";

//component List sendo chamdo para o componenete Pai App
import { List } from "./components/List";

//component TotalMoney sendo chamdo para o componenete Pai App
import { TotalMoney } from "./components/TotalMoney";

//Biblioteca (yarn add uuid) para adicionar um id único na aplicaçao a ser criada
import { v4 as uuid } from "uuid";


// Funcao Pai App recebe sua lógica
function App() {

  //Estado para transicao da lista
  const [listTransactions, setListTransactions] = useState([]);

  //Estado para mudança de pagina perceba que o estado recebe o true entao o homePage vale true
  const [homePage, setHomePage] = useState(true);

  //Estado para filtrar a opçao de botoes 'todos' | 'entradas' | 'despesas' ele sai valendo 'todos'
  const [currentFilter, setCurrentFilter] = useState("todos"); 

  //Funcao adici
  function addTransaction({ description, value, type }) {
    setListTransactions([
      ...listTransactions,
      {
        description,
        value,
        type,
        id: uuid(),
      },
    ]);
  }

  function removeTransaction(id) {
    const newList = listTransactions.filter(
      (transaction) => transaction.id !== id
    );
    setListTransactions(newList);
  }

  return homePage ? (
    <HomePage setHomePage={setHomePage} />
  ) : (
    <>
      <Header setHomePage={setHomePage} />
      <main className="main__container">
        <div>
          {/* <FaUserCircle /> */}
          <Form addTransaction={addTransaction} />
          {console.log(addTransaction)}
          {listTransactions.length > 0 && (
            <TotalMoney listTransactions={listTransactions} />
          )}
        </div>

        <div className="container__section">
          <div className="section__cards--elements">
            <h3 className="section__cards--title">Resumo Financeiro</h3>
            <button
              className={currentFilter === "todos" ? "btns active" : "btns"}
              onClick={() => setCurrentFilter("todos")}
            >
              Todos
            </button>
            <button
              className={currentFilter === "entradas" ? "btns active" : "btns"}
              onClick={() => setCurrentFilter("entradas")}
            >
              Entradas
            </button>
            <button
              className={currentFilter === "despesas" ? "btns active" : "btns"}
              onClick={() => setCurrentFilter("despesas")}
            >
              Despesas
            </button>
          </div>

          <List
            listTransactions={listTransactions}
            currentFilter={currentFilter}
            removeTransaction={removeTransaction}
          />
        </div>
      </main>
    </>
  );
}

export default App;
