export const TotalMoney = ({ value }) => {
  return (
    <div>
      {value.length !== "" ? (
        <div className="cont">
          <div className="cont__element">
            <h3>Valor Total:</h3>
            <p>R$ {value}</p>
          </div>
          <p>O valor se refere ao saldo</p>
        </div>
      ) : (
        <></>
      )}
    </div>
  );
};
