        event.preventDefault()
        console.log(inputDescription)
    }

    return (
    <form onSubmit={handelSubmit}>
      <p>Descricao</p>
      <input onChange={(event)=> setInputDescription(event.target.value)} placeholder="digite aqui" />
      <span>aqui</span>
