import { useState } from "react";
import { Card } from "../Card";
import "./style.css";
import NoCard from "../../assets/NoCard.png";
import { ButtonsFilter } from "../Buttons";

export const List = ({ listTransactions }) => {
  const [filter, setFilter] = useState("");

  const filtered = (listTransactions, type, setFilter) => {
  listTransactions.filter((list) =>
    list.type.includes(filter || list.type)
  );
  }

  return (
    <section className="section__cards">
      <div className="section__cards--elements">
        <h3 className="section__cards--title">Resumo financeiro</h3>
        <div className="section__cards--btns">
          <ButtonsFilter
            onClick={() => setFilter("")}
            className="btns"
            type="Todos"
          >
            Todosyarn
          </ButtonsFilter>
          <ButtonsFilter
            onClick={() => setFilter("entrada")}
            className="btns"
            type="Entradas"
          >
            Entradas
          </ButtonsFilter>
          <ButtonsFilter
            onClick={() => setFilter("saida")}
            className="btns"
            type="Despesas"
          >
            Despesas
          </ButtonsFilter>
        </div>
      </div>
      {listTransactions.length > 0 ? (
        <>
          {filtered.map((list, index) => (
            <Card
              key={index}
              description={list.description}
              type={list.type}
              value={list.value}
            ></Card>
          ))}
        </>
      ) : (
        <ul className="section__cards--ul">
          <h2 className="section__cards--text">
            Você ainda não possui nenhum lançamento
          </h2>
          <img className="logoVazia" src={NoCard} alt="logoCardVazio" />
          <img className="logoVazia" src={NoCard} alt="logoCardVazio" />
          <img className="logoVazia" src={NoCard} alt="logoCardVazio" />
        </ul>
      )}
    </section>
  );
};
