import { useState } from "react";
import { Form } from "./components/Form";
import { Header } from "./components/Header/";
import { HomePage } from "./pages/HomePage";
import { List } from "./components/List";
import { TotalMoney } from "./components/TotalMoney";

function FilterButton({ children, currentFilter, setCurrentFilter }) {
  const buttonFilter = children.toLowerCase();
  console.log(buttonFilter)
  return (
    <button
      className={currentFilter === buttonFilter ? "btns active" : "btns"}
      onClick={() => setCurrentFilter(buttonFilter)}
    >
      {children}
    </button>
  );
}

function App() {
  const [listTransactions, setListTransactions] = useState([]);

  const [homePage, setHomePage] = useState(true);
  const [currentFilter, setCurrentFilter] = useState("todos"); // 'todos' | 'entradas' | 'despesas'

  return homePage ? (
    <HomePage setHomePage={setHomePage} />
  ) : (
    <>
      <Header setHomePage={setHomePage} />
      <main className="main__container">
        <Form
          listTransactions={listTransactions}
          setListTransactions={setListTransactions}
        />
        {listTransactions.length > 0 ? (
          <TotalMoney listTransactions={listTransactions} />
        ) : (
          0
        )}
        <div>
          <p>Resumo financeiro</p>
          <FilterButton
            currentFilter={currentFilter}
            setCurrentFilter={setCurrentFilter}
          >
            Todos
          </FilterButton>
          <FilterButton
            currentFilter={currentFilter}
            setCurrentFilter={setCurrentFilter}
          >
            Entradas
          </FilterButton>
          <FilterButton
            currentFilter={currentFilter}
            setCurrentFilter={setCurrentFilter}
          >
            Saidas
          </FilterButton>
        </div>

        <List
          listTransactions={listTransactions}
          currentFilter={currentFilter}
        />
      </main>
    </>
  );
}

export default App;
